public class Ex04 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i=10;
		while(i<10){
			System.out.println("while¹® = "+i);
			i=i+1;
		}
		//-------------------------------------
		i=10;
		do{
			System.out.println("do-while¹® = "+i);
			i=i+1;
		}while(i<10);
	}

}
